import React from 'react';
import './About.css'; 
import profilepic from './profilepic.png';

const AboutPage = () => {
  const skills = [
    { name: 'JavaScript', level: '90%' },
    { name: 'React', level: '85%' },
    { name: 'Python', level: '80%' },
    { name: 'Java', level: '75%' },
    { name: 'C/C++', level: '70%' },
  ];

  const experiences = [
    {
      title: 'Web Developer at XYZ Company',
      description: 'Developed and maintained web applications, improved user interfaces, and collaborated with cross-functional teams.'
    },
    // Add more experiences dynamically if needed
  ];

  const projects = [
    {
      title: 'Project 1',
      description: 'Description of Project 1'
    },
    {
      title: 'Project 2',
      description: 'Description of Project 2'
    },
    // Add more projects dynamically if needed
  ];

  const education = [
    {
      degree: 'B.Tech in Computer Science',
      institution: 'XYZ University'
    },
    // Add more education details dynamically if needed
  ];

  return (
    <div className="about">
      <section className="intro">
        <div className="intro-content">
          <img src={profilepic} alt="Profile" className="profile-pic" />
          <div className="intro-text">
            <h1>About Me</h1>
            <p>I’m Soumyadeep Maity, a passionate web developer based in Kolkata, India. With a keen interest in cutting-edge technology and design, I’ve been working on various projects that showcase my skills and creativity. I love listening and playing music, good cinema, and am also passionate about video games. My favorite sport is motorsports, and Lewis Hamilton is my favorite driver.</p>
          </div>
        </div>
      </section>
      <section className="skills">
        <h2>Skills</h2>
        <div className="skill-list">
          {skills.map(skill => (
            <div className="skill-item" key={skill.name}>
              <h3>{skill.name}</h3>
              <div className="skill-bar" style={{ width: skill.level }}></div>
            </div>
          ))}
        </div>
      </section>
      <section className="experience">
        <h2>Experience & Projects</h2>
        <div className="experience-list">
          {experiences.map(exp => (
            <div className="experience-item" key={exp.title}>
              <h3>{exp.title}</h3>
              <p>{exp.description}</p>
            </div>
          ))}
          {projects.map(proj => (
            <div className="experience-item" key={proj.title}>
              <h3>{proj.title}</h3>
              <p>{proj.description}</p>
            </div>
          ))}
        </div>
      </section>
      <section className="education">
        <h2>Education</h2>
        <div className="education-list">
          {education.map(edu => (
            <div className="education-item" key={edu.degree}>
              <h3>{edu.degree}</h3>
              <p>{edu.institution}</p>
            </div>
          ))}
        </div>
      </section>
      <section className="contact-info">
        <h2>Contact Information</h2>
        <p>Email: soumadeepmaity2@gmail.com</p>
        <p>Phone: 9830218177</p>
      </section>
    </div>
  );
};

export default AboutPage;
