import React, { useEffect, useState } from 'react';
import './AboutSection.css';

const AboutSection = () => {
  const texts = [
    "Soumyadeep Maity . . .",
    "A Web Developer . . .",
    "A Software Engineer . . .",
    "An Artist . . .",
  ];
  const [currentText, setCurrentText] = useState('');
  const [index, setIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [charIndex, setCharIndex] = useState(0);

  useEffect(() => {
    const handleTyping = () => {
      if (isDeleting) {
        if (charIndex > 0) {
          setCharIndex(charIndex - 1);
          setCurrentText(currentText.slice(0, charIndex - 1));
        } else {
          setIsDeleting(false);
          setIndex((index + 1) % texts.length);
        }
      } else {
        if (charIndex < texts[index].length) {
          setCharIndex(charIndex + 1);
          setCurrentText(currentText + texts[index].charAt(charIndex));
        } else {
          setTimeout(() => setIsDeleting(true), 2500); // Increased pause duration
        }
      }
    };

    const typingSpeed = isDeleting ? 50 : 100; // Adjusted typing speed
    const typingTimer = setTimeout(handleTyping, typingSpeed);

    return () => clearTimeout(typingTimer);
  }, [charIndex, isDeleting, currentText, index, texts]);

  return (
    <div className="aboutsection">
      <div className="intro-anim-container">
        <div className="intro-anim pop-in">
          <h2>I am</h2>
          <div className="intro-anim-text">
            <span className="typed-text">{currentText}</span>
            <span className="cursor">|</span>
          </div>
        </div>
      </div>
      
      <div className="intro-content pop-in">
        <h1>About Me</h1>
        <p>
        I'm a passionate web developer based in Kolkata, India, with a keen interest in cutting-edge technology and design. I’ve worked on various projects that showcase my skills and creativity.

For a deeper dive into my work, visit my [GitHub](https://github.com/ALmoSTGoD249/soumyadeepmaity). There, you'll find repositories showcasing my latest projects, including web development work, AI chatbot projects, and other programming endeavors. My GitHub is a testament to my commitment to continuous learning and innovation in the tech space.

Feel free to explore my GitHub to see what I’m up to!
        </p>
      </div>
    </div>
  );
};

export default AboutSection;
