// src/components/PortfolioSection.js
import React from 'react';
import Slider from 'react-slick';
import './PortfolioSection.css';

// Import project images
import snakeGameImg from './snake-game-image.png'; // Update the paths as necessary
import songSiftRichImg from './song-sift-rich-image.png';
import songSiftGuiImg from './song-sift-gui-image.png';
import hagridChatbotImg from './hagrid-chatbot-image.png';
import hagridVaImg from './hagrid-va-image.png';

const PortfolioSection = () => {
  // Carousel settings
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
  };

  return (
    <section className="portfolio">
      <h2>My Work</h2>
      <Slider {...settings} className="portfolio-slider">
        <div className="portfolio-item">
          <img src={snakeGameImg} alt="Snake Game" />
          <h3>Snake Game</h3>
          <p>A classic Snake game built using JavaScript.</p>
          <a href="https://github.com/ALmoSTGoD249/Snake-Game" target="_blank" rel="noopener noreferrer">View on GitHub</a>
        </div>
        <div className="portfolio-item">
          <img src={songSiftRichImg} alt="SongSift-Rich" />
          <h3>SongSift-Rich</h3>
          <p>A music recommendation app with a rich UI.</p>
          <a href="https://github.com/ALmoSTGoD249/SongSift-Rich" target="_blank" rel="noopener noreferrer">View on GitHub</a>
        </div>
        <div className="portfolio-item">
          <img src={songSiftGuiImg} alt="SongSift-GUI" />
          <h3>SongSift-GUI</h3>
          <p>A GUI version of the SongSift app with enhanced features.</p>
          <a href="https://github.com/ALmoSTGoD249/SongSift-GUI" target="_blank" rel="noopener noreferrer">View on GitHub</a>
        </div>
        <div className="portfolio-item">
          <img src={hagridChatbotImg} alt="Hagrid-Chatbot" />
          <h3>Hagrid-Chatbot</h3>
          <p>A chatbot with advanced capabilities powered by OpenAI.</p>
          <a href="https://github.com/ALmoSTGoD249/Hagrid-Chatbot" target="_blank" rel="noopener noreferrer">View on GitHub</a>
        </div>
        <div className="portfolio-item">
          <img src={hagridVaImg} alt="Hagrid-VA" />
          <h3>Hagrid-VA</h3>
          <p>A virtual assistant with voice recognition features.</p>
          <a href="https://github.com/ALmoSTGoD249/Hagrid-VA" target="_blank" rel="noopener noreferrer">View on GitHub</a>
        </div>
      </Slider>
    </section>
  );
};

export default PortfolioSection;
