// src/pages/Home.js
import React, { useRef } from 'react';
import { useSpring, animated } from '@react-spring/web';
import Header from '../components/Header'; // Adjust path as necessary
import HeroSection from '../components/HeroSection'; // Adjust path as necessary
import AboutSection from '../components/AboutSection'; // Adjust path as necessary
import PortfolioSection from '../components/PortfolioSection'; // Adjust path as necessary
import ContactSection from '../components/ContactSection'; // Adjust path as necessary

const Home = () => {
  const ref = useRef(null);
  const [props, api] = useSpring(() => ({
    opacity: 0,
    transform: 'translateY(20px)',
  }));

  React.useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            api.start({
              opacity: 1,
              transform: 'translateY(0px)',
            });
          } else {
            api.start({
              opacity: 0,
              transform: 'translateY(20px)',
            });
          }
        });
      },
      { threshold: 0.1 }
    );

    if (ref.current) {
      observer.observe(ref.current);
    }

    return () => {
      if (ref.current) {
        observer.unobserve(ref.current);
      }
    };
  }, [api]);

  return (
    <div>
        <HeroSection />
        <animated.div style={props} ref={ref}>
        <AboutSection />
        <PortfolioSection />
        <ContactSection />
      </animated.div>
    </div>
  );
};

export default Home;
