// src/components/PortfolioSection.js
import React from 'react';
import Slider from 'react-slick';
import './PortfolioSection.css';

const PortfolioSection = () => {
  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Show 3 slides at a time
    slidesToScroll: 1,
    centerMode: true,
    centerPadding: '0', // Remove padding on the sides
    arrows: true,
    prevArrow: <button className="slick-prev">‹</button>,
    nextArrow: <button className="slick-next">›</button>,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  return (
    <section className="portfolio">
      <h2>My Work</h2>
      <div className="portfolio-slider">
        <Slider {...settings}>
          <div className="portfolio-item">
            <img src="https://github.com/ALmoSTGoD249/Snake-Game/raw/main/screenshot.png" alt="Snake Game" />
            <h3>Snake Game</h3>
            <p>A classic Snake game with modern features.</p>
            <a href="https://github.com/ALmoSTGoD249/Snake-Game" target="_blank" rel="noopener noreferrer">View on GitHub</a>
          </div>
          <div className="portfolio-item">
            <img src="https://github.com/ALmoSTGoD249/SongSift-Rich/raw/main/screenshot.png" alt="SongSift-Rich" />
            <h3>SongSift-Rich</h3>
            <p>A music recommendation app with a rich UI.</p>
            <a href="https://github.com/ALmoSTGoD249/SongSift-Rich" target="_blank" rel="noopener noreferrer">View on GitHub</a>
          </div>
          <div className="portfolio-item">
            <img src="https://github.com/ALmoSTGoD249/SongSift-GUI/raw/main/screenshot.png" alt="SongSift-GUI" />
            <h3>SongSift-GUI</h3>
            <p>A music recommendation app with a polished GUI.</p>
            <a href="https://github.com/ALmoSTGoD249/SongSift-GUI" target="_blank" rel="noopener noreferrer">View on GitHub</a>
          </div>
          <div className="portfolio-item">
            <img src="https://github.com/ALmoSTGoD249/Hagrid-Chatbot/raw/main/screenshot.png" alt="Hagrid-Chatbot" />
            <h3>Hagrid-Chatbot</h3>
            <p>An AI chatbot with advanced functionalities.</p>
            <a href="https://github.com/ALmoSTGoD249/Hagrid-Chatbot" target="_blank" rel="noopener noreferrer">View on GitHub</a>
          </div>
          <div className="portfolio-item">
            <img src="https://github.com/ALmoSTGoD249/Hagrid-VA/raw/main/screenshot.png" alt="Hagrid-VA" />
            <h3>Hagrid-VA</h3>
            <p>A virtual assistant with voice recognition.</p>
            <a href="https://github.com/ALmoSTGoD249/Hagrid-VA" target="_blank" rel="noopener noreferrer">View on GitHub</a>
          </div>
        </Slider>
      </div>
    </section>
  );
};

export default PortfolioSection;
