import React, { useEffect, useState } from 'react';
import './AboutSection.css';

const AboutSection = () => {
  const texts = [
    "Soumyadeep Maity . . .",
    "A Web Developer . . .",
    "A Software Engineer . . .",
    "An Artist . . .",
  ];
  const [currentText, setCurrentText] = useState('');
  const [index, setIndex] = useState(0);
  const [isDeleting, setIsDeleting] = useState(false);
  const [charIndex, setCharIndex] = useState(0);

  useEffect(() => {
    const handleTyping = () => {
      if (isDeleting) {
        if (charIndex > 0) {
          setCharIndex(charIndex - 1);
          setCurrentText(currentText.slice(0, charIndex - 1));
        } else {
          setIsDeleting(false);
          setIndex((index + 1) % texts.length);
        }
      } else {
        if (charIndex < texts[index].length) {
          setCharIndex(charIndex + 1);
          setCurrentText(currentText + texts[index][charIndex]);
        } else {
          setIsDeleting(true);
        }
      }
    };

    const typingSpeed = isDeleting ? 50 : 150;
    const typingTimeout = setTimeout(handleTyping, typingSpeed);

    return () => clearTimeout(typingTimeout);
  }, [charIndex, currentText, index, isDeleting, texts]);

  return (
    <div className="aboutsection">
      <div className="intro-row">
        <div className="intro-anim">
          <div className="intro-anim-text">
            <span className="typed-text">{currentText}</span>
            <span className="cursor">|</span>
          </div>
        </div>
        <div className="intro-content">
          <h1>About Me</h1>
          <p>
            I'm a passionate web developer and software engineer. I love creating innovative solutions and engaging user experiences.
          </p>
        </div>
      </div>
    </div>
  );
};

export default AboutSection;
