// src/pages/Projects.js
import React from 'react';
import PortCard from '../components/PortCard';
import './Projects.css';

// List of all projects with their titles, descriptions, and images
const projects = [
  {
    title: 'Snake Game',
    description: 'A classic Snake game implemented with JavaScript and HTML5 Canvas.',
    image: require('../components/snake-game-image.png').default // Ensure the path is correct
  },
  {
    title: 'SongSift-Rich',
    description: 'A feature-rich application for discovering and recommending music using Spotify API.',
    image: require('../components/song-sift-gui-image.png').default // Ensure the path is correct
  },
  {
    title: 'SongSift-GUI',
    description: 'A graphical user interface for the SongSift application with a polished design.',
    image: require('../components/song-sift-gui-image.png').default // Ensure the path is correct
  },
  {
    title: 'Hagrid-Chatbot',
    description: 'An AI-powered chatbot built with OpenAI’s GPT models for interactive conversations.',
    image: require('../components/hagrid-chatbot-image.png').default // Ensure the path is correct
  },
  {
    title: 'Hagrid-VA',
    description: 'Voice Assistant using OpenAI’s APIs with speech recognition and automation features.',
    image: require('..//hagrid-va-image.png').default // Ensure the path is correct
  }
  // Add more projects if needed
];

function Projects() {
  return (
    <div className="projects">
      <header>
        <h1>My Projects</h1>
      </header>
      <div className="card-container">
        {projects.length > 0 ? (
          projects.map((project, index) => (
            <PortCard 
              key={index}
              title={project.title}
              description={project.description}
              image={project.image}
            />
          ))
        ) : (
          <p>No projects available</p>
        )}
      </div>
    </div>
  );
}

export default Projects;
