// src/pages/Home.js
import React from 'react';
import { useSpring, animated } from '@react-spring/web';
import Header from '../components/Header'; // Adjust path as necessary
import HeroSection from '../components/HeroSection'; // Adjust path as necessary
import AboutSection from '../components/AboutSection'; // Adjust path as necessary
import PortfolioSection from '../components/PortfolioSection'; // Adjust path as necessary
import ContactSection from '../components/ContactSection'; // Adjust path as necessary

const Home = () => {
  const [props, api] = useSpring(() => ({
    opacity: 50,
    transform: 'translateY(20px)',
    from: { opacity:50, transform: 'translateY(20px)' },
  }));

  React.useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      api.start({
        opacity: scrollY > 100 ? 1 : 0,
        transform: scrollY > 100 ? 'translateY(0px)' : 'translateY(20px)',
      });
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [api]);

  return (
    <div>
      <Header />
        <HeroSection />
        <animated.div style={props}>
        <AboutSection />
        <animated.div style={props}></animated.div>
        <PortfolioSection />
        <animated.div style={props}></animated.div>
        <ContactSection />
      </animated.div>
    </div>
  );
};

export default Home;
